import React from "react";
import DoiSongImg from "../assets/image/banner/banner.png";
import Topic_Post from "./Topic_Post";
import { useSelector, useDispatch } from "react-redux";
import PostSlice from "../features/post/PostSlice";
import { getAllBlogsByUserID } from "../../../server/service/blog.service";

export default function Topic(props) {
  const allPosts = useSelector(state => state.post);
  const [userID,setUserID] = useS
  console.log(allPosts);
  const dispatch = useDispatch();
    useEffect(() => {
        dispatch(getAllBlogsByUserID())
    },[dispatch])
    const allTasksElements = allTasks.map(task => {
        return(
            <Task key={task.id}
                id={task.id}
                content={task.content}
                isdone={task.isdone} />
        )
    })
  
    const allPostsElements = allPosts.map((post) => {
      return (
        <Topic_Post
              title={post.title}
              content = {post.content}
        />
      );
    });
  return (
    <div className="container">
      <div className="topic">
        <h2 className="topic-name">{props.topicName}</h2>
        <div className="topic-underline"></div>
      </div>

      <div>
       {allPostsElements}
      </div>
    </div>
  );
}
