import React from "react";

const MessageBox = ({ title, body, blogName, slug }) => {
	const [isCheck, setIsCheck] = React.useState(false);
	function handleClick() {
		<Link to={`/${params.alias}/blog/${props.slug}`}>
      <div className="category_container-image" ref={bodyImageRef}>
        <img onLoad={onImgLoad} src={props.urlImage} alt="" />
      </div>
      <div className="category_container-content">
        <h3>{props.date}</h3>
        <h1>{props.title}</h1>
        <p>{props.content}</p>
      </div>
    </Link>;
	}

	return (
		<div className="message-box">
			<div className="message-box__container">
				<div className="message-box__icon">
					<i className="bx bxs-message-alt-error"></i>
				</div>
				<div className="message-box__content">
					<div className="message-box__content__title">{title}</div>
					<div className="message-box__content__body">{body} "{blogName}"</div>
				</div>
				<div className="message-box__close">
					<i className="bx bx-check" onClick={handleClick}></i>
					<i className="bx bx-x"></i>
				</div>
			</div>
		</div>
	);
};

export default MessageBox;
