import { useState } from 'react';
import { animated, useSpring } from 'react-spring';

function Checkbox({ content }) {
    const [isChecked, setIsChecked] = useState(false);const checkboxAnimationStyle = useSpring({
        backgroundColor: isChecked ? '#808' : '#fff',
        borderColor: isChecked ? '#808' : '#ddd',
    });

    return (
        <label>
            <input
                type="checkbox"
                onChange={() => {
                    setIsChecked(!isChecked);
                }}
            />
            <animated.svg
                className={`checkbox ${isChecked ? 'checkbox--active' : ''}`}
                // This element is purely decorative so
                // we hide it for screen readers
                aria-hidden="true"
                viewBox="0 0 15 11"
                fill="none"
            >
                <animated.path
                    d="M1 4.5L5 9L14 1"
                    strokeWidth="2"
                    stroke={isChecked ? '#fff' : 'none'} // only show the checkmark when `isCheck` is `true`
                />
            </animated.svg>
            {content}
        </label>
    );
}

export default Checkbox;
