import React from "react";
import BlogCard from "./BlogCard";
import { useSelector, useDispatch } from "react-redux";
import { getAllBlogsByUserIDAsync } from "../features/post/blogSlice";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function Topic(props) {
	// const dispatch = useDispatch();
	// const userID = 13;
	// const topicID = props.id;
	// const allBlogs = useSelector((state) => state.blog);
	// useEffect(() => {
	// 	dispatch(getAllBlogsByUserIDAsync(userID));
	// }, [dispatch, userID]);
	// const allBlogsByTopic = allBlogs?.filter((blog) => blog.topicID === topicID);
	// console.log(allBlogsByTopic);
	const allBlogsByTopic = props.blogs;
	// console.log(allBlogsByTopic)
	const mainBlog = allBlogsByTopic[0];

	const allPostsElements = allBlogsByTopic.slice(1, 3).map((blog) => {
		return (
			// eslint-disable-next-line react/jsx-pascal-case
			<BlogCard
				key={blog.id}
				id={blog.id}
				urlImage={require(`../assets/image/blog/${blog.coverImg}`)}
				content={blog.content}
				title={blog.title}
				slug={blog.slug}
			/>
		);
	});

	return (
		<div className="topicCard" id={props.id}>
			<div className="topic">
				<div className="topic__title">
					{props.id % 2 === 0 ? (
						<Link to={`/${props.alias}/topic/${props.slug}`}>
							{props.topicName.toUpperCase()}
						</Link>
					) : (
						""
					)}
					<div className="topic__line"></div>
					{props.id % 2 === 1 ? (
						<Link to={`/${props.alias}/topic/${props.slug}`}>
							{props.topicName.toUpperCase()}
						</Link>
					) : (
						""
					)}
				</div>
			</div>
			<div className="featured-blogs">
				<div className="fetured-blogs__main-blog">
					<div className="blog-card__image">
						<div className="blog-card__image__container" ref={bodyImageRef}>
							<img onLoad={onImgLoad} src={require(`../assets/image/blog/${blog.coverImg}`)} alt="" />
						</div>
					</div>
					<div className="blog-card__content">
						<div className="blog-card__content__border"></div>
						<h3>{props.title}</h3>
						<p>{props.content}</p>
					</div>
				</div>
				<div>{allPostsElements}</div>
			</div>
		</div>
	);
}
