import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import Axios from "axios";

export const getAllBlogsByUserIDAsync = createAsyncThunk(
	"blogs/getAllBlogsByUserIDAsync",
	async (userID) => {
		const response = await Axios.get(
			`http://localhost:5000/api/blog?userID=${userID}`
		);
		const tasks = response.data;
		return { tasks };
	}
);

export const accountSlice = createSlice({
	name: "account",
	initialState: {
		account: null,
	},
	reducers: {
		login: (state, action) => {
			const data = {
				username: action.payload.username,
				password: action.payload.password,
			};
			Axios.post("http://localhost:5000/auth", { data })
				.then((res) => {
					if (res) {
						state.account = action.payload;
						console.log(state.account);
					} else state.account = null;
				})
				.catch((error) => {
					console.log(error);
				});
		},
		logout: (state) => {
			state.account = null;
		},
	},
});

export const { login, logout } = accountSlice.actions;

export const selectAccount = (state) => state.account.account;

export default accountSlice.reducer;
