import React from 'react';
import Helmet from '../components/Helmet';
import Topic from '../components/Topic';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

import alt from '../assets/image/user/alt.png';

function Home() {
    // const allTopics = useSelector((state) => state.topic);
    // const user = JSON.parse(localStorage.getItem('user'))?.account;
    useEffect(() => {
        const fetchData = async () => {
            const commentUserID = [
                ...new Set(comments.map((comment) => comment.userID)),
            ];
            console.log(commentUserID);

            await Promise.all(
                commentUserID.map(async (userID) => {
                    const result = (
                        await axios(
                            `${serverUrl}api/user/get-user-by-id?userID=${userID}`
                        )
                    ).data.result;
                    setCommentUser([...commentUser, result]);
                    commentUser.push(result);
                })
            );
            console.log(commentUser);
        };
        fetchData();
    }, [comments]);
	
    const user = useSelector((state) => state.user);
    const blogger = useSelector((state) => state.blogger);

	const topic = blogger.alias === user.alias ? user.Topic:blogger.Topic;

       const allTopicElements = blogger._id && topic.map((topic, index) => {
            return (
                <Topic
                    key={index}
                    index={index}
                    id={topic._id}
                    userID={topic.userID}
                    topicName={topic.topicName}
                    slug={topic.slug}
                    blogs={topic.Blog}
                    alias={blogger.alias}
                />
            );
        });

	var isLoggedIn = false;
	user && (isLoggedIn = (blogger.alias === user.alias));
    return (
        <div>
            <Helmet title="Trang chủ">
                {
                    <div className="home">
                        {isLoggedIn ? (
                            <div className="home__new-blog">
                                <Link
                                    to={`/${user.alias}/newBlog`}
                                    className="home__link"
                                >
                                    <div className="home__new-blog__pfp">
                                        {blogger.profilepic !== '' &&
                                        blogger.profilepic !== undefined ? (
                                            <img
                                                src={
                                                    'data:image/jpg;base64,' +
                                                    blogger.profilepic
                                                }
                                                alt={alt}
                                            />
                                        ) : (
                                            <img src={alt} alt={alt} />
                                        )}
                                    </div>
                                    <div className="home__new-blog__placeholder">
                                        {'Tạo nguồn cảm hứng mới <3'}
                                    </div>
                                </Link>
                            </div>
                        ) : (
                            ''
                        )}
                        <div className="home__topics">{allTopicElements}</div>
                    </div>
                }
            </Helmet>
        </div>
    );
}

export default Home;
