import React, { useRef, useState } from 'react';
import { useDispatch } from 'react-redux';
import { createNewComment, getAllCommentsByBlogAsync } from '../features/comment/commentSlice';

const Reply = ({blogID, userID}) => {
    const dispatch = useDispatch();
    const textareaRef = useRef();
    const [context, setContext] = useState('');

    const makeNewComment = async () => {
        await dispatch(
            createNewComment({
                context: context,
                parentID: '',
                blogID: blogID,
                userID: userID,
            })
        );
        await dispatch(getAllCommentsByBlogAsync(blogID));
    };


    return (
        <div className="reply">
            <div className="reply__container">
                <input
                    className="reply__input"
                    type="text"
                    onChange={(e) => setContext(e.target.value)}
                />
                <textarea
                    type="text"
                    name="content"
                    className="new-blog__form__body"
                    placeholder="Ná»™i dung*"
                    ref={textareaRef}
                    onChange={(e) => setContext(e.target.value)}
                    onKeyUp={(e) => {
                        textareaRef.current.style.height = '80px';
                        let height = e.target.scrollHeight;
                        if (height > 200) {
                            // textareaRef.current.style.height = "auto";
                            textareaRef.current.style.height = `${height}px`;
                        }
                    }}
                ></textarea>
                <div className="reply__post-button" onClick={makeNewComment}>
                    Comment
                </div>
            </div>
        </div>
    );
};

export default Reply;
