import React, { useRef, useState } from 'react';
import { useDispatch } from 'react-redux';
import {
    createNewComment,
    getAllCommentsByBlogAsync,
} from '../features/comment/commentSlice';

const Reply = ({ blogID, userID, headComment }) => {
    const dispatch = useDispatch();
    const textareaRef = useRef();
    const [context, setContext] = useState('');

    const makeNewComment = async () => {
        if (context) {
            await dispatch(
                createNewComment({
                    context: context,
                    parentID: '',
                    blogID: blogID,
                    userID: userID,
                })
            );
            await dispatch(getAllCommentsByBlogAsync(blogID));
        }
    };

    return (
        <div className="reply">
            <div className="reply__connect-line"></div>
            <div className="reply__container">
                {/* <input
                    className="reply__input"
                    type="text"
                    onChange={(e) => setContext(e.target.value)}
                /> */}
                <div className="reply__pfp"></div>
                <div className="reply__comment-area">
                    <textarea
                        type="text"
                        name="content"
                        className="reply__input"
                        placeholder="Leave a comment"
                        ref={textareaRef}
                        onChange={(e) => setContext(e.target.value)}
                        onBlur={(e) => {
                            if (e.target.value === '')
                                textareaRef.current.style.height = '45px';
                        }}
                        onInput={(e) => {
                            // textareaRef.current.style.height = '60px';
                            let height = e.target.scrollHeight;
                            if (height > 60 && e.target.value !== '') {
                                // textareaRef.current.style.height = "auto";
                                textareaRef.current.style.height = `${height}px`;
                            }
                        }}
                    ></textarea>
                    <div
                        className="reply__post-button"
                        onClick={makeNewComment}
                    >
                        Comment
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Reply;
