import React from "react";
import { useState } from "react";
import { useRef } from "react";
import { useDispatch } from "react-redux";
import namingBlogSlug from "../utils/namingSlugs";
import { createNewBlogAsync } from "../features/post/blogSlice";
import Helmet from "../components/Helmet";
import { useNavigate } from "react-router-dom";

const NewBlog = ({ alias }) => {
	function TopicMenu(props) {
		const dropdownItemClick = (e) => {
			e.preventDefault();
		};
		return (
			<div className="dropdown__item" onClick={dropdownItemClick}>
				<span className="dropdown__item__icon-button">{props.leftIcon}</span>
				<p>{props.children}</p>
				<span className="dropdown__item__icon-right">{props.rightIcon}</span>
			</div>
		);
	}

	const textareaRef = useRef();
	const imageRef = useRef();
	const dispatch = useDispatch();
	const navigate = useNavigate();

	const initialBlogState = {
		title: "",
		content: "",
		quote: "",
		date: new Date().toLocaleDateString(),
		location: "",
		slug: "",
		topicID: "25",
		coverImg: "huonglehere_doi-song_danh-bai.jpg",
	};

	const [newBlog, setNewBlog] = useState(initialBlogState);

	const handleChange = (input) => (event) => {
		setNewBlog({ ...newBlog, [input]: event.target.value });
	};

	const handleTitleChange = () => (event) => {
		setNewBlog({
			...newBlog,
			["title"]: event.target.value,
			["slug"]: namingBlogSlug(event.target.value),
		});
	};

	// const [image, setImage] = useState();

	const readImage = (e) => {
		const image = e.target.files[0];
		console.log(image);
	};

	const imageUploadHandler = (e) => {};

	const handleNewBlog = (e) => {
		e.preventDefault();
		dispatch(createNewBlogAsync({ blogReqData: newBlog }));
		navigate(`/${alias}/`);
	};

	return (
		<Helmet title="Tạo blog mới">
			<div className="new-blog">
				<form className="new-blog__form" onSubmit={handleNewBlog}>
					<input
						type="file"
						ref={imageRef}
						onChange={readImage}
						className="new-blog__form__image"
					/>
					<TopicMenu>a</TopicMenu>
					<input
						type="text"
						name="quote"
						onChange={handleChange("quote")}
						className="new-blog__form__quote"
						placeholder="Châm ngôn"
					/>
					<input
						type="text"
						name="location"
						className="new-blog__form__place"
						placeholder="Địa điểm"
						onChange={handleChange("location")}
					/>
					<input
						type="text"
						name="title"
						className="new-blog__form__title"
						placeholder="Tiêu đề"
						onChange={handleTitleChange()}
					/>
					<textarea
						type="text"
						name="content"
						className="new-blog__form__body"
						placeholder="Nội dung"
						ref={textareaRef}
						onChange={handleChange("content")}
						onKeyUp={(e) => {
							textareaRef.current.style.height = "200px";
							let height = e.target.scrollHeight;
							if (height > 200) {
								// textareaRef.current.style.height = "auto";
								textareaRef.current.style.height = `${height}px`;
							}
						}}></textarea>
					<input
						type="text"
						className="new-blog__form__signature"
						placeholder="Chữ ký"
					/>
					<button className="new-blog__form__create-button" type="submit">
						Tạo bài viết
					</button>
				</form>
			</div>
		</Helmet>
	);
};

export default NewBlog;
