import React from 'react';
import { useState } from 'react';

const Comment = ({ comment, alias, pfp }) => {
    const [context, setContext] = useState();
    console.log(comment.children);

    return (
        <div className="comment">
            <div className="comment__card">
                <div className="comment__card__left">
                    <div className="comment__card__left__pfp"></div>
                </div>
                <div className="comment__card__right">
                    <div className="comment__card__right__title">
                        <div className="comment__card__right__alias"></div>
                        <div className="comment__card__right__timestamp">
                            {comment.timestamp}
                        </div>
                    </div>
                    <div className="comment__card__right__context">
                        {comment.context}
                    </div>
                    <div className="comment__card__right__options">
                        <div className="comment__card__right__options__reply"></div>
                    </div>
                    <div className="comment__card__right__replies">
                        {comment.children.map((child) => {
                            <Comment comment={child} key={comment._id} />;
                        })}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Comment;
