import React from "react";
import DoiSongImg from "../assets/image/banner/banner.png";
import Topic_Post from "./Topic_Post";
import { useSelector, useDispatch } from "react-redux";
import PostSlice from "../features/post/PostSlice";
import { getAllBlogsByUserIDAs } from "../../../server/service/blog.service";
import { useState } from "react";

export default function Topic(props) {
  const allPosts = useSelector(state => state.post);
  const [userID, setUserID] = useState(1);
  console.log(allPosts);
  const dispatch = useDispatch();
    useEffect(() => {
        dispatch(getAllBlogsByUserID(userID))
    },[dispatch])
    const allPostsElements = allPosts.map(post => {
        return(
            <Topic_Post key={post.id}
                id={post.id}
                content={post.content}
                title={post.title} />
        )
    })
  return (
    <div className="container">
      <div className="topic">
        <h2 className="topic-name">{props.topicName}</h2>
        <div className="topic-underline"></div>
      </div>

      <div>
       {allPostsElements}
      </div>
    </div>
  );
}
